export function useEntityNarrativeEvents(
  entityId: string | null | undefined
): NarrativeEvent[] {
  const ensureEntityEvents = useNarrativeStore((state) => state.ensureEntityEvents);
  const backend = useNarrativeStore((state) => state.backend);
  const simulationRunId = useNarrativeStore((state) => state.simulationRunId);
  useEffect(() => {
    if (!entityId) return;
    if (!backend || !simulationRunId) return;
    void ensureEntityEvents(entityId);
  }, [backend, entityId, ensureEntityEvents, simulationRunId]);

  return useNarrativeStore(
    (state) => (entityId ? state.eventsByEntity.get(entityId) ?? EMPTY_EVENTS : EMPTY_EVENTS)
  );
}

export function useNarrativeStatus(): NarrativeStatus {
  return useNarrativeStore((state) => state.status);
}

export function useNarrativeLoading(): boolean {
  return useNarrativeStore((state) => state.status.loading);
}

export function useEntityNarrativeLoading(entityId: string | null | undefined): boolean {
  return useNarrativeStore((state) => {
    if (!entityId) return false;
    return state.loadingEntityIds.has(entityId);
  });
}

export function useEntityNarrativeReady(entityId: string | null | undefined): boolean {
  return useNarrativeStore((state) => {
    if (!entityId) return true;
    return state.loadedEntityIds.has(entityId);
  });
}

export function useAllNarrativeEvents(): NarrativeEvent[] {
  return useNarrativeStore((state) => Array.from(state.eventsById.values()));
}

export function useAllNarrativeEventsLoading(): boolean {
  return useNarrativeStore((state) => state.allEventsLoading);
}

export function useAllNarrativeEventsLoaded(): boolean {
  return useNarrativeStore((state) => state.allEventsLoaded);
}

/* =========================================
   ParchmentTexture component
   Loads real parchment/vellum photos, processes
   them through the canvas pipeline, and displays
   as a repeating background with soft-light blend.
   ========================================= */

export function ParchmentTexture({
  className,
  config = DEFAULT_PARCHMENT_CONFIG,
  prebakedUrl
}: Readonly<{
  className?: string;
  config?: ParchmentConfig;
  /** Pre-baked tile URL — skips runtime canvas pipeline when provided */
  prebakedUrl?: string;
}>) {
  const [textureUrl, setTextureUrl] = useState<string | null>(prebakedUrl ?? null);
  const [prevUrl, setPrevUrl] = useState<string | null>(null);

  // Regenerate when processing params change — opacity is CSS-only
  // Skip entirely when a prebaked tile is provided
  const genKey = JSON.stringify({
    br: config.blurRadius,
    ds: config.detailStrength
  });
  useEffect(() => {
    if (prebakedUrl) return;
    let cancelled = false;
    async function generate() {
      try {
        const [parchmentImg, vellumImg] = await Promise.all([loadImage(parchmentSrc), loadImage(vellumSrc)]);
        if (cancelled) return;

        // Normalize both to working size
        const pCanvas = drawScaled(parchmentImg, WORK_SIZE, WORK_SIZE);
        const vCanvas = drawScaled(vellumImg, WORK_SIZE, WORK_SIZE);

        // Pipeline: frequency blend → mirror tile
        // Keep natural warm tones — soft-light blend adds organic warmth
        const blended = frequencyBlend(pCanvas, vCanvas, config.blurRadius, config.detailStrength);
        const tileable = mirrorTile(blended);
        const url = await canvasToObjectURL(tileable);
        if (!cancelled) {
          setTextureUrl(url);
        }
      } catch (err) {
        console.error("[ParchmentTexture] Failed to generate:", err);
      }
    }
    void generate();
    return () => {
      cancelled = true;
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [prebakedUrl, genKey]);

  // Revoke previous object URL to avoid memory leaks (only for generated URLs)
  useEffect(() => {
    if (prevUrl && prevUrl !== textureUrl && prevUrl !== prebakedUrl) {
      URL.revokeObjectURL(prevUrl);
    }
    setPrevUrl(textureUrl);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [textureUrl]);

  // Cleanup on unmount (only for generated URLs)
  useEffect(() => {
    return () => {
      if (textureUrl && textureUrl !== prebakedUrl) URL.revokeObjectURL(textureUrl);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  if (!textureUrl) return null;

  // Mirror tile is 2x WORK_SIZE
  const tileSize = WORK_SIZE * 2;
  return <div aria-hidden className={`${className ?? ""} ${ornStyles.parchmentTexture}`}
  // eslint-disable-next-line local/no-inline-styles -- dynamic texture URL and opacity from canvas pipeline
  style={{
    "--parchment-url": `url(${textureUrl})`,
    "--parchment-size": `${tileSize}px ${tileSize}px`,
    "--parchment-opacity": config.opacity
  } as React.CSSProperties} />;
}

