      <circle cx="97" cy="1" r="0.9" fill="#a8ccd8" opacity="0.5" />
      <circle cx="137" cy="0" r="1.0" fill="#d0e8f0" opacity="0.6" />
      <circle cx="177" cy="2" r="0.8" fill="#a8ccd8" opacity="0.5" />
      <circle cx="217" cy="1" r="0.9" fill="#a8ccd8" opacity="0.5" />
    </svg>;
}

