/**
 * ToolUsageBadges component
 *
 * @param {Object} props
 * @param {Object} props.usage - Object with usage counts by tool
 *   e.g., { nameforge: 3, coherence: 1 }
 * @param {boolean} props.compact - If true, show only icons without labels
 * @param {boolean} props.showZero - If true, show badges even when count is 0
 */
export function ToolUsageBadges({ usage = {}, compact = false, showZero = false }) {
  const badges = Object.entries(usage)
    .filter(([_type, count]) => showZero || count > 0)
    .filter(([type]) => BADGE_CONFIG[type]);

  if (badges.length === 0) {
    return null;
  }

  return (
    <div className="tool-badge-container">
      {badges.map(([type, count]) => {
        const config = BADGE_CONFIG[type];
        return (
          <span
            key={type}
            className={`tool-badge ${config.className}`}
            title={`${config.tooltip}${count > 1 ? ' (' + count + ' uses)' : ''}`}
          >
            <span className="tool-badge-icon">{config.icon}</span>
            {!compact && <span className="tool-badge-label">{config.label}</span>}
            {count > 1 && <span className="tool-badge-count">&times;{count}</span>}
          </span>
        );
      })}
    </div>
  );
}

/**
 * DetailUsageBadges component
 *
 * @param {Object} props
 * @param {Object} props.usage - Object with arrays of referencing items
 *   e.g., { generators: ['gen1', 'gen2'], systems: ['sys1'] }
 * @param {boolean} props.showOrphan - If true, show "Not used" when no usage (default: true)
 */
export function DetailUsageBadges({ usage = {}, showOrphan = true }) {
  const badges = [];

  for (const [type, config] of Object.entries(BADGE_CONFIG)) {
    const items = usage[type];
    if (items?.length > 0) {
      const count = items.length;
      const label = type === 'eras' && count !== 1 ? config.labelPlural : config.label;
      badges.push(
        <span
          key={type}
          className={`detail-badge ${config.className}`}
          title={`${config.tooltip}: ${items.join(', ')}`}
        >
          {count} {label}
        </span>
      );
    }
  }

  if (badges.length === 0) {
    if (showOrphan) {
      return <span className="detail-badge detail-badge-orphan">Not used</span>;
    }
    return null;
  }

  return <div className="detail-badge-container">{badges}</div>;
}

/**
 * @param {Object} props
 * @param {string} props.value - Current selected value
 * @param {Function} props.onChange - Callback when selection changes
 * @param {Array<{value: string, label?: string, meta?: string}>} props.options - Array of options
 * @param {string} [props.placeholder] - Placeholder text for empty selection
 * @param {string} [props.label] - Optional label above the dropdown
 * @param {boolean} [props.searchable] - Enable searchable popover mode (default: false)
 * @param {string} [props.className] - Additional class names
 */
export function ReferenceDropdown({
  value,
  onChange,
  options,
  placeholder,
  label,
  searchable = false,
  className = '',
}) {
  const generatedId = useId();
  // Searchable mode state
  const [isOpen, setIsOpen] = useState(false);
  const [search, setSearch] = useState('');
  const containerRef = useRef(null);

  // Close on outside click (searchable mode)
  useEffect(() => {
    if (!searchable) return;

    const handleClickOutside = (e) => {
      if (containerRef.current && !containerRef.current.contains(e.target)) {
        setIsOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [searchable]);

  // Filter options in searchable mode
  const filteredOptions = useMemo(() => {
    if (!searchable || !search) return options;
    const lower = search.toLowerCase();
    return options.filter(
      (opt) =>
        opt.value.toLowerCase().includes(lower) ||
        opt.label?.toLowerCase().includes(lower)
    );
  }, [options, search, searchable]);

  const selectedOption = options.find((opt) => opt.value === value);

  // Simple mode: render a standard <select>
  if (!searchable) {
    return (
      <div className={`form-group ${className}`.trim()}>
        {label && <label htmlFor={generatedId} className="label">{label}</label>}
        <select id={generatedId}
          className="select"
          value={value || ''}
          onChange={(e) => onChange(e.target.value || undefined)}
        >
          <option value="">{placeholder || 'Select...'}</option>
          {options.map((opt) => (
            <option key={opt.value} value={opt.value}>
              {opt.label || opt.value}
            </option>
          ))}
        </select>
      </div>
    );
  }

  // Searchable mode: render popover with search
  return (
    <div className={`form-group ${className}`.trim()}>
      {label && <label className="label">{label}</label>}
      <div ref={containerRef} className="dropdown">
        <div
          className="dropdown-trigger"
          onClick={() => setIsOpen(!isOpen)}
          role="button"
          tabIndex={0}
          onKeyDown={(e) => { if (e.key === "Enter" || e.key === " ") e.currentTarget.click(); }}
        >
          <span className={selectedOption ? '' : 'dropdown-trigger-placeholder'}>
            {selectedOption?.label ||
              selectedOption?.value ||
              placeholder ||
              'Select...'}
          </span>
          <span className="dropdown-arrow">▼</span>
        </div>
        {isOpen && (
          <div className="dropdown-menu">
            <div className="dropdown-search">
              <input
                type="text"
                className="dropdown-search-input"
                placeholder="Search..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                onClick={(e) => e.stopPropagation()}
                // eslint-disable-next-line jsx-a11y/no-autofocus
                autoFocus
              />
            </div>
            <div className="dropdown-options">
              {filteredOptions.length === 0 ? (
                <div className="dropdown-empty">No options found</div>
              ) : (
                filteredOptions.map((opt) => (
                  <div
                    key={opt.value}
                    className={`dropdown-option ${value === opt.value ? 'dropdown-option-selected' : ''}`}
                    onClick={() => {
                      onChange(opt.value);
                      setIsOpen(false);
                      setSearch('');
                    }}
                    role="button"
                    tabIndex={0}
                    onKeyDown={(e) => { if (e.key === "Enter" || e.key === " ") e.currentTarget.click(); }}
                  >
                    <span>{opt.label || opt.value}</span>
                    {opt.meta && (
                      <span className="dropdown-option-meta">{opt.meta}</span>
                    )}
                  </div>
                ))
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

/**
 * @param {Object} props
 * @param {string[]} props.value - Array of selected values
 * @param {Function} props.onChange - Callback when selection changes
 * @param {Array<{value: string, label?: string}>} props.options - Available options
 * @param {string} [props.placeholder] - Placeholder text when empty
 * @param {string} [props.label] - Optional label above the component
 */
export function ChipSelect({
  value = [],
  onChange,
  options,
  placeholder = 'Add...',
  label,
}) {
  const [isOpen, setIsOpen] = useState(false);
  const [search, setSearch] = useState('');
  const containerRef = useRef(null);
  const inputRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (containerRef.current && !containerRef.current.contains(e.target)) {
        setIsOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const availableOptions = useMemo(() => {
    return options.filter(opt => !value.includes(opt.value));
  }, [options, value]);

  const filteredOptions = useMemo(() => {
    if (!search) return availableOptions;
    const lower = search.toLowerCase();
    return availableOptions.filter(opt =>
      opt.value.toLowerCase().includes(lower) ||
      opt.label?.toLowerCase().includes(lower)
    );
  }, [availableOptions, search]);

  const handleSelect = (optValue) => {
    onChange([...value, optValue]);
    setSearch('');
    inputRef.current?.focus();
  };

  const handleRemove = (optValue) => {
    onChange(value.filter(v => v !== optValue));
  };

  const getLabel = (val) => {
    const opt = options.find(o => o.value === val);
    return opt?.label || val;
  };

  return (
    <div className="form-group">
      {label && <label className="label">{label}</label>}
      <div ref={containerRef} className="dropdown">
        <div
          className="chip-container chip-container-input"
          onClick={() => {
            setIsOpen(true);
            inputRef.current?.focus();
          }}
          role="button"
          tabIndex={0}
          onKeyDown={(e) => { if (e.key === "Enter" || e.key === " ") e.currentTarget.click(); }}
        >
          {value.map(v => (
            <span key={v} className="chip">
              {getLabel(v)}
              <button
                type="button"
                className="chip-remove"
                onClick={(e) => {
                  e.stopPropagation();
                  handleRemove(v);
                }}
              >
                ×
              </button>
            </span>
          ))}
          <input
            ref={inputRef}
            type="text"
            placeholder={value.length === 0 ? placeholder : ''}
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            onFocus={() => setIsOpen(true)}
            className="chip-input"
          />
        </div>
        {isOpen && filteredOptions.length > 0 && (
          <div className="dropdown-menu">
            {filteredOptions.map((opt) => (
              <div
                key={opt.value}
                onClick={() => handleSelect(opt.value)}
                className="dropdown-item"
                role="button"
                tabIndex={0}
                onKeyDown={(e) => { if (e.key === "Enter" || e.key === " ") e.currentTarget.click(); }}
              >
                {opt.label || opt.value}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

/**
 * @param {Object} props
 * @param {Array<{id: string, name?: string}>} props.items - Items to select from
 * @param {Function} props.onSelect - Called with item ID when selected
 * @param {string} [props.placeholder] - Placeholder text for trigger
 * @param {string} [props.emptyMessage] - Message when no items available
 * @param {string} [props.noMatchMessage] - Message when search has no matches
 * @param {boolean} [props.searchable] - Whether to show search input (default true)
 * @param {string} [props.className] - Additional class names
 */
export function SearchableDropdown({
  items,
  onSelect,
  placeholder = 'Select...',
  emptyMessage = 'No items available',
  noMatchMessage = 'No matches found',
  searchable = true,
  className = '',
}) {
  const [isOpen, setIsOpen] = useState(false);
  const [search, setSearch] = useState('');
  const containerRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (containerRef.current && !containerRef.current.contains(e.target)) {
        setIsOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const filteredItems = useMemo(() => {
    if (!search) return items;
    const lower = search.toLowerCase();
    return items.filter(item =>
      item.id.toLowerCase().includes(lower) ||
      item.name?.toLowerCase().includes(lower)
    );
  }, [items, search]);

  const handleSelect = (item) => {
    onSelect(item.id);
    setIsOpen(false);
    setSearch('');
  };

  if (items.length === 0) {
    return (
      <div className={`dropdown-trigger dropdown-trigger-disabled ${className}`.trim()}>
        <span>{emptyMessage}</span>
      </div>
    );
  }

  return (
    <div ref={containerRef} className={`dropdown ${className}`.trim()}>
      <div className="dropdown-trigger" onClick={() => setIsOpen(!isOpen)} role="button" tabIndex={0} onKeyDown={(e) => { if (e.key === "Enter" || e.key === " ") e.currentTarget.click(); }} >
        <span>{placeholder}</span>
        <span className="dropdown-arrow">▼</span>
      </div>
      {isOpen && (
        <div className="dropdown-menu">
          {searchable && (
            <div className="dropdown-search">
              <input
                type="text"
