/**
 * @param {Object} props
 * @param {number|string} props.value - Current value
 * @param {Function} props.onChange - Called when value changes
 * @param {Array<{value: number|string, label: string, color: string}>} props.levels - Level definitions
 * @param {boolean} [props.showNumeric] - Show numeric input (default false, only for numeric values)
 * @param {number} [props.min] - Minimum value for numeric input (default 0)
 * @param {number} [props.max] - Maximum value for numeric input (default 10)
 * @param {number} [props.step] - Step for numeric input (default 0.1)
 * @param {string} [props.className] - Additional class names
 */
export function LevelSelector({
  value,
  onChange,
  levels,
  showNumeric = false,
  min = 0,
  max = 10,
  step = 0.1,
  className = '',
}) {
  const [hoveredLevel, setHoveredLevel] = useState(null);

  // Detect if using numeric or string mode
  const isNumeric = typeof levels[0]?.value === 'number';

  // Get level index based on value
  const getLevelIndex = (val) => {
    if (isNumeric) {
      for (let i = levels.length - 1; i >= 0; i--) {
        if (val >= levels[i].value) return i;
      }
      return 0;
    } else {
      const idx = levels.findIndex(l => l.value === val);
      return idx >= 0 ? idx : 0;
    }
  };

  const levelIndex = getLevelIndex(value);
  const currentLevel = levels[levelIndex];
  const hoverLevel = hoveredLevel !== null ? levels[hoveredLevel] : null;

  // Calculate partial fill for each dot (only meaningful for numeric mode)
  const getPartialFill = (idx) => {
    if (!isNumeric) {
      // String mode: full fill up to and including current level
      return idx <= levelIndex ? 1 : 0;
    }
    if (idx < levelIndex) return 1;
    if (idx > levelIndex) return 0;
    // Current level - calculate partial based on value position
    const levelStart = levels[idx].value;
    const levelEnd = idx < levels.length - 1 ? levels[idx + 1].value : max;
    const progress = (value - levelStart) / (levelEnd - levelStart);
    return Math.max(0, Math.min(1, progress));
  };

  return (
    <div className={`level-selector ${className}`.trim()}>
      <div className="level-selector-dots">
        {levels.map((level, idx) => {
          const isHovered = hoveredLevel !== null && idx <= hoveredLevel;
          const fill = getPartialFill(idx);
          const baseColor = isHovered ? hoverLevel.color : currentLevel.color;

          return (
            <div
              key={idx}
              className={`level-selector-dot ${hoveredLevel === idx ? 'level-selector-dot-active' : ''}`.trim()}
              onClick={() => onChange(level.value)}
              onMouseEnter={() => setHoveredLevel(idx)}
              onMouseLeave={() => setHoveredLevel(null)}
              title={`${level.label}`}
              role="button"
              tabIndex={0}
              onKeyDown={(e) => { if (e.key === "Enter" || e.key === " ") e.currentTarget.click(); }}
            >
              <svg className="level-selector-dot-fill-svg" viewBox="0 0 100 100" preserveAspectRatio="none" aria-hidden="true">
                <rect
                  x="0"
                  y={100 - (fill * 100)}
                  width="100"
                  height={fill * 100}
                  fill={baseColor}
                />
              </svg>
            </div>
          );
        })}
      </div>
      {showNumeric && isNumeric && (
        <input
          type="number"
          className="level-selector-input"
          value={value}
          onChange={(e) => {
            const newVal = parseFloat(e.target.value);
            if (!isNaN(newVal)) {
              onChange(Math.max(min, Math.min(max, newVal)));
            }
          }}
          step={step}
          min={min}
          max={max}
        />
      )}
    </div>
  );
}

/**
 * @param {Object} props
 * @param {number|undefined|null} props.value - The numeric value
 * @param {Function} props.onChange - Called with the parsed number when valid
 * @param {string} [props.className] - CSS class for the input
 * @param {number} [props.min] - Minimum value
 * @param {number} [props.max] - Maximum value
 * @param {number|string} [props.step] - Step value for increment/decrement
 * @param {string} [props.placeholder] - Placeholder text
 * @param {boolean} [props.allowEmpty] - If true, empty string calls onChange(undefined)
 * @param {boolean} [props.integer] - If true, only allow integers
 * @param {boolean} [props.disabled] - Disable the input
 */
export function NumberInput({
  value,
  onChange,
  className = 'input',
  min,
  max,
  step: _step,
  placeholder,
  allowEmpty = false,
  integer = false,
  disabled = false,
  ...rest
}) {
  const externalDisplayValue = formatValue(value);
  // Internal string state for editing
  const [localValue, setLocalValue] = useState(() => externalDisplayValue);
  // Track focus in state so render can safely choose draft vs external value
  const [isFocused, setIsFocused] = useState(false);

  const parseValue = useCallback((str) => {
    if (str === '' || str === '-' || str === '.' || str === '-.') {
      return null; // Intermediate state, not a valid number yet
    }
    const parsed = integer ? parseInt(str, 10) : parseFloat(str);
    if (isNaN(parsed)) return null;
    return parsed;
  }, [integer]);

  const handleFocus = useCallback(() => {
    setLocalValue(externalDisplayValue);
    setIsFocused(true);
  }, [externalDisplayValue]);

  const handleChange = useCallback((e) => {
    const newValue = e.target.value;

    // Allow empty, minus sign, decimal point, or any numeric pattern
    // This regex allows intermediate states like "-", ".", "-.", "1.", "-1."
    /* eslint-disable sonarjs/slow-regex -- short user input (single number field value) */
    const validPattern = integer
      ? /^-?\d*$/  // Integer: optional minus, digits only
      : /^-?\d*\.?\d*$/; // Float: optional minus, digits, optional decimal, more digits
    /* eslint-enable sonarjs/slow-regex */

    if (!validPattern.test(newValue)) {
      return; // Reject invalid characters
    }

    setLocalValue(newValue);

    // Try to parse and update parent if it's a complete valid number
    const parsed = parseValue(newValue);
    if (parsed !== null) {
      // Apply min/max constraints
      let constrained = parsed;
      if (min !== undefined && constrained < min) constrained = min;
      if (max !== undefined && constrained > max) constrained = max;
      onChange(constrained);
    } else if (allowEmpty && newValue === '') {
      onChange(undefined);
    }
  }, [onChange, min, max, allowEmpty, integer, parseValue]);

  const handleBlur = useCallback(() => {
    setIsFocused(false);

    // On blur, ensure the display value matches the actual value
    const parsed = parseValue(localValue);
    if (parsed !== null) {
      // Apply constraints and update
      let constrained = parsed;
      if (min !== undefined && constrained < min) constrained = min;
      if (max !== undefined && constrained > max) constrained = max;
      setLocalValue(formatValue(constrained));
      onChange(constrained);
    } else if (allowEmpty && localValue === '') {
      onChange(undefined);
    } else {
      // Revert to the parent's value if local is invalid
      setLocalValue(externalDisplayValue);
    }
  }, [allowEmpty, externalDisplayValue, localValue, max, min, onChange, parseValue]);

  return (
    <input
      type="text"
      inputMode="numeric"
      value={isFocused ? localValue : externalDisplayValue}
      onChange={handleChange}
      onFocus={handleFocus}
      onBlur={handleBlur}
      className={className}
      placeholder={placeholder}
      disabled={disabled}
      {...rest}
    />
  );
}

export function LocalTextArea({
  value,
  onChange,
  className = 'textarea',
  placeholder,
  rows,
  ...rest
}) {
  const externalValue = value || '';
  const [localValue, setLocalValue] = useState(externalValue);
  // Track focus in state so render can safely choose between local draft vs external value
  const [isFocused, setIsFocused] = useState(false);

  const handleFocus = useCallback(() => {
    setLocalValue(externalValue);
    setIsFocused(true);
  }, [externalValue]);

  // Call onChange on blur if value changed
  const handleBlur = useCallback(() => {
    setIsFocused(false);
    if (localValue !== externalValue) {
      onChange(localValue);
    }
  }, [externalValue, localValue, onChange]);

  return (
    <textarea
      value={isFocused ? localValue : externalValue}
      onChange={(e) => setLocalValue(e.target.value)}
      onFocus={handleFocus}
      onBlur={handleBlur}
      className={className}
      placeholder={placeholder}
      rows={rows}
      {...rest}
    />
  );
}

export default function CoverageMatrix({
  rows = [],
  columns = [],
  getCellValue,
  getCellDisplay,
  onRowClick,
  onCellClick,
  title = 'Coverage Matrix',
  subtitle = '',
  stats = [],
  legend = [],
  searchPlaceholder = 'Search...',
  groupByField = 'group',
  columnHeaderClass,
  emptyMessage = 'No data to display.',
  filterOptions = [],
}) {
  const [searchQuery, setSearchQuery] = useState('');
  const [activeFilter, setActiveFilter] = useState(null);

  // Normalize columns to { id, label } format
  const normalizedColumns = useMemo(() => {
    return columns.map((col) =>
      typeof col === 'string' ? { id: col, label: col } : col
    );
  }, [columns]);

  // Filter rows by search
  const filteredRows = useMemo(() => {
    let result = rows;

    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      result = result.filter(
        (row) =>
          row.id.toLowerCase().includes(query) ||
          row.label.toLowerCase().includes(query) ||
          (row.groupLabel && row.groupLabel.toLowerCase().includes(query))
      );
    }

    if (activeFilter && filterOptions.length > 0) {
      const filterDef = filterOptions.find((f) => f.id === activeFilter);
      if (filterDef?.filter) {
        result = result.filter(filterDef.filter);
      }
    }

    return result;
  }, [rows, searchQuery, activeFilter, filterOptions]);

  // Group rows if groupByField is specified
  const groupedRows = useMemo(() => {
    if (!groupByField) {
      return { _ungrouped: { label: '', rows: filteredRows } };
    }

    const groups = {};
    filteredRows.forEach((row) => {
      const groupId = row[groupByField] || '_ungrouped';
      const groupLabel = row.groupLabel || groupId;
      if (!groups[groupId]) {
        groups[groupId] = { label: groupLabel, rows: [] };
      }
      groups[groupId].rows.push(row);
    });
    return groups;
  }, [filteredRows, groupByField]);

  // Default cell display function
  const defaultGetCellDisplay = (value) => {
    switch (value) {
      case 'primary':
        return { icon: '✓', className: 'primary', title: 'Primary' };
      case 'secondary':
        return { icon: '○', className: 'secondary', title: 'Secondary' };
      case 'both':
        return { icon: '◉', className: 'both', title: 'Both' };
      case 'none':
      default:
        return { icon: '-', className: 'none', title: 'None' };
    }
  };

  const displayFn = getCellDisplay || defaultGetCellDisplay;

  return (
    <div className="coverage-matrix">
      {/* Header */}
      <div className="cm-header">
        <h2 className="cm-title">{title}</h2>
        {subtitle && <p className="cm-subtitle">{subtitle}</p>}
      </div>

      {/* Stats Bar */}
      {stats.length > 0 && (
        <div className="cm-stats">
          {stats.map((stat, idx) => (
            <div key={idx} className={`cm-stat ${stat.variant || ''}`}>
              <span className="cm-stat-value">{stat.value}</span>
              <span className="cm-stat-label">{stat.label}</span>
            </div>
          ))}
        </div>
      )}

      {/* Toolbar */}
      <div className="cm-toolbar">
        <input
          type="text"
          className="cm-search"
          placeholder={searchPlaceholder}
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        {filterOptions.length > 0 && (
          <div className="cm-filters">
            {filterOptions.map((filter) => (
              <button
                key={filter.id}
                className={`cm-filter-btn ${activeFilter === filter.id ? 'active' : ''}`}
                onClick={() =>
                  setActiveFilter(activeFilter === filter.id ? null : filter.id)
                }
              >
                {filter.label}
              </button>
            ))}
          </div>
        )}
      </div>

      {/* Matrix Grid */}
      <div className="cm-container">
        {(normalizedColumns.length === 0 || rows.length === 0) && (
          <div className="cm-empty">{emptyMessage}</div>
        )}
        {normalizedColumns.length > 0 && rows.length > 0 && filteredRows.length === 0 && (
          <div className="cm-empty">No items match the current filters.</div>
        )}
        {normalizedColumns.length > 0 && rows.length > 0 && filteredRows.length > 0 && (
          <table className="cm-table">
            <thead>
              <tr>
                <th className="cm-group-col">Group</th>
                <th className="cm-label-col">Name</th>
                <th className="cm-status-col">Status</th>
                {normalizedColumns.map((col) => (
                  <th
                    key={col.id}
                    className={`cm-data-col ${columnHeaderClass?.(col.id) || ''}`}
                    title={col.label}
                  >
                    {col.label}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {Object.entries(groupedRows).map(([groupId, { label: groupLabel, rows: groupRows }]) =>
                groupRows.map((row, idx) => (
                  <MatrixRow
                    key={row.id}
                    row={row}
                    idx={idx}
                    groupId={groupId}
                    groupLabel={groupLabel}
                    columns={normalizedColumns}
                    getCellValue={getCellValue}
                    displayFn={displayFn}
                    onRowClick={onRowClick}
                    onCellClick={onCellClick}
                  />
                ))
              )}
            </tbody>
          </table>
        )}
      </div>

      {/* Legend */}
      {legend.length > 0 && (
        <div className="cm-legend">
          {legend.map((item, idx) => (
            <span key={idx} className="cm-legend-item">
              <span className={`cm-cell-icon sample ${item.className || ''}`}>
                {item.icon}
              </span>
              {item.label}
            </span>
          ))}
        </div>
      )}
    </div>
  );
}

/**
 * @param {Object} props
 * @param {boolean} props.enabled - Current toggle state
 * @param {Function} props.onChange - Callback when toggle changes (receives new value)
 * @param {Function} [props.onClick] - Optional raw click handler (receives event, takes priority)
 * @param {string} [props.label] - Optional label beside the toggle
 * @param {string} [props.className] - Optional additional class names
 */
export function EnableToggle({ enabled, onChange, onClick, label, className = '' }) {
  const handleClick = (e) => {
    if (onClick) {
      onClick(e);
    } else if (onChange) {
      onChange(!enabled);
    }
  };

  return (
    <div className={`flex items-center gap-lg ${className}`.trim()}>
      <div
        onClick={handleClick}
        className={`toggle ${enabled ? 'toggle-on' : ''}`}
        role="button"
        tabIndex={0}
        onKeyDown={(e) => { if (e.key === "Enter" || e.key === " ") handleClick(e); }}
      >
        <div className="toggle-knob" />
      </div>
      {label && (
        <span className="text-md text-muted">{label}</span>
      )}
    </div>
  );
}

